<?phpclass Api_branch_list_model extends CI_Controller{		function get_data($city_name="",$limit="",$offset=""){						$this->db->select("*");		$this->db->from("branch a");		$this->db->join("city b","b.city_id=a.city_id");				if($city_name !=""){			$this->db->where("b.city_name",$city_name);		}				$this->db->where("a.deleted","0");				if($limit !="" OR $offset !=""){			$this->db->limit($limit,$offset);		}				$query=$this->db->get();				return $query->result_array();	}	}