<?phpclass Api_discount_list_model extends CI_Controller{		function get_data($name){				$this->db->select("*");		$this->db->from("discount a");		$this->db->join("branch b","b.branch_id=a.branch_id");		$this->db->where("a.deleted","0");		$this->db->where("b.branch_name",$name);		$query = $this->db->get();						if($query->num_rows() > 0){			foreach($query->result_array() as $row){				$data["day"]=$row["day"];												$treat = explode(",",$row["treatment_id"]);				$price = explode(",",$row["discount_price"]);								$this->db->select("*");				$this->db->from("treatment a");				$this->db->join("treatment_price b","b.treatment_id=a.treatment_id");				$this->db->where("a.deleted","0");							$this->db->where_in("a.treatment_id",$treat);							$this->db->group_by("a.treatment_id");				$query2 = $this->db->get();								$data["treat"]=$query2->result_array();				$data["disc"]=$price;				$data["treats"]=$treat;							$all[]=$data;			}										}else{			$all = array();		}				return $all;	}	}