<?phpclass Api_master_list_model extends CI_Controller{		function get_city($limit="",$offset=""){		$this->db->where("deleted","0");		if($limit !="" OR $offset !=""){			$this->db->limit($limit,$offset);		}		$query=$this->db->get("city");						return $query->result_array();	}		function get_branch($city_name="",$limit="",$offset=""){			$this->db->select("*");		$this->db->from("branch a");		$this->db->join("city b","b.city_id=a.city_id");				if($city_name !=""){			$this->db->where("b.city_name",$city_name);		}				$this->db->where("a.deleted","0");				if($limit !="" OR $offset !=""){			$this->db->limit($limit,$offset);		}				$query=$this->db->get();				return $query->result_array();	}		function get_stylist($branch_name="",$limit="",$offset=""){		$this->db->select("*");		$this->db->from("stylist a");		$this->db->join("branch b","b.branch_id=a.branch_id");				if($branch_name !=""){			$this->db->where("b.branch_name",$branch_name);		}				$this->db->where("a.deleted","0");				if($limit !="" OR $offset !=""){			$this->db->limit($limit,$offset);		}				$query=$this->db->get();				return $query->result_array();	}		function get_treatment($branch_name="",$limit="",$offset=""){		$this->db->select("*");		$this->db->from("treatment_price a");		$this->db->join("branch b","b.branch_id=a.branch_id");		$this->db->join("treatment c","c.treatment_id=a.treatment_id");				if($branch_name !=""){			$this->db->where("b.branch_name",$branch_name);		}				$this->db->where("a.deleted","0");				if($limit !="" OR $offset !=""){			$this->db->limit($limit,$offset);		}				$query=$this->db->get();				return $query->result_array();	}}