<?phpclass Api_stylist_list_model extends CI_Controller{		function get_data($branch_name="",$limit="",$offset=""){		$this->db->select("*");		$this->db->from("stylist a");		$this->db->join("branch b","b.branch_id=a.branch_id");				if($branch_name !=""){			$this->db->where("b.branch_name",$branch_name);		}				$this->db->where("a.deleted","0");				if($limit !="" OR $offset !=""){			$this->db->limit($limit,$offset);		}				$query=$this->db->get();				return $query->result_array();	}}